mapper.select:
select nvl(max(anum),0) anum
from artist
where artist=#{artist}

select anum
from artist
where artist=#{artist}

select a.anum, a.artist
from artist a, game_artist ga
where a.anum = ga.anum and ga.gnum = #{gnum}

select count(*)
from game_artist ga, artist a
where ga.anum = a.anum and ga.gnum = #{gnum} and a.artist = #{artist}

select a.artist
	from artist a, game_artist ga
	where a.anum = ga.anum and ga.gnum = #{gnum}
