mapper.select:
SELECT gnum, bggnum, GAMETITLE, GAMETITLE_ENG, TOTALVIEWCOUNT, RELEASE_YEAR, gamephoto_url, game_rank, rate, weight, bgg_thumbnail, tot, theme, game_rank_month FROM ( SELECT a.gnum, a.bggnum, a.gametitle, a.gametitle_eng, a.totalviewcount, a.release_year, a.bgg_thumbnail, a.game_rank, a.gamephoto_url, a.rate, a.weight,a.tot, a.theme, a.game_rank_month FROM ( SELECT g.gnum, g.bggnum, g.gametitle, g.gametitle_eng, g.totalviewcount, g.release_year,g.theme, g.bgg_thumbnail, g.game_rank, ga.fullname AS gamephoto_url, gr.rate, gr.weight, gr.tot, g.game_rank_month FROM game g LEFT JOIN ( SELECT gnum, fullname FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY gnum ORDER BY create_date DESC) rnum, gnum, fullname FROM game_attach ) WHERE rnum = 1 ) ga ON g.gnum = ga.gnum LEFT JOIN ( SELECT gnum, avg(rating) as rate, avg(weight) as weight, count(*) tot FROM game_rating GROUP BY gnum ) gr ON g.gnum = gr.gnum WHERE g.del = 'n'
ORDER BY game_rank ) a ) WHERE game_rank BETWEEN #{start} AND #{end}
ORDER BY game_rank_month ) a ) WHERE game_rank_month BETWEEN #{start} AND #{end}

mapper.update:
update 	game
set 	viewcount = viewcount+1, totalviewcount = totalviewcount+1
where 	gnum=#{gnum}

mapper.select:
    	select 	g.gnum, bggnum, gametitle, gametitle_eng, players, playtime, ages, language, bgg_thumbnail,
         	   	release_year, to_char(g.create_date, 'yyyy-mm-dd') as create_date, totalviewcount, ga.fullname gamephoto_url, game_rank, theme
		from 	game g
		left join 	(
        	   		select 	gnum, fullname
              		from 	(
              				select row_number() over(partition by gnum order by create_date desc) rnum, gnum, fullname
                    		from game_attach
                    		)
             		where 	rnum = 1
             		) ga
        on 		g.gnum = ga.gnum
      	where 	g.gnum=#{gnum}


select count(*)


		from game_category mapping, category filter


		from ${mappingTable} mapping, ${filter} filter


, (select gnum from game where del='n') g


		mapping.anum = filter.anum and filter.anum=#{num}


		mapping.cnum = filter.cnum and filter.cnum=#{num}


		mapping.dnum = filter.dnum and filter.dnum=#{num}


		mapping.mnum = filter.mnum and filter.mnum=#{num}


		mapping.pnum = filter.pnum and filter.pnum=#{num}

	and mapping.gnum=g.gnum

SELECT gnum, bggnum, GAMETITLE, GAMETITLE_ENG, TOTALVIEWCOUNT, RELEASE_YEAR, gamephoto_url,bgg_thumbnail, nameStr, rate, weight, game_rank, tot, theme, game_rank_month, rn FROM ( SELECT a.gnum, a.bggnum, a.gametitle, a.gametitle_eng, a.totalviewcount, a.release_year, a.bgg_thumbnail, a.gamephoto_url, a.nameStr, a.rate, a.weight,a.game_rank, a.tot, rownum rn, a.theme, a.game_rank_month FROM ( SELECT g.gnum, g.bggnum, g.gametitle, g.gametitle_eng, g.totalviewcount, g.release_year, g.bgg_thumbnail,g.game_rank, ga.fullname AS gamephoto_url, b.${filter} as nameStr, gr.rate, gr.weight, gr.tot, g.theme, g.game_rank_month FROM game g LEFT JOIN ( SELECT gnum, fullname FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY gnum ORDER BY create_date DESC) rnum, gnum, fullname FROM game_attach ) WHERE rnum = 1 ) ga ON g.gnum = ga.gnum LEFT JOIN ( SELECT gnum, avg(rating) as rate, avg(weight) as weight, count(*) as tot FROM game_rating GROUP BY gnum ) gr ON g.gnum = gr.gnum left join (
select gnum, f.${filter}, f.anum num from ${mappingTable} m, ${filter} f where m.anum = f.anum
select gnum, f.${filter}, f.cnum num from game_category m, category f where m.cnum = f.cnum
select gnum, f.${filter}, f.dnum num from ${mappingTable} m, ${filter} f where m.dnum = f.dnum
select gnum, f.${filter}, f.mnum num from ${mappingTable} m, ${filter} f where m.mnum = f.mnum
select gnum, f.${filter}, f.pnum num from ${mappingTable} m, ${filter} f where m.pnum = f.pnum
) b on g.gnum = b.gnum WHERE g.del = 'n' and b.num=#{num}
ORDER BY game_rank ) a ) WHERE rn BETWEEN #{start} AND #{end}
ORDER BY game_rank_month ) a ) WHERE rn BETWEEN #{start} AND #{end}

SELECT gnum, bggnum, GAMETITLE, GAMETITLE_ENG, TOTALVIEWCOUNT, RELEASE_YEAR, gamephoto_url,bgg_thumbnail, nameStr, rate, weight, tot, theme, rn, game_rank FROM ( SELECT a.gnum, a.bggnum, a.gametitle, a.gametitle_eng, a.totalviewcount, a.release_year, a.bgg_thumbnail, a.gamephoto_url, a.nameStr, a.rate, a.weight, a.tot,rownum rn, a.rcount, a.game_rank, a.theme FROM ( SELECT g.gnum, g.bggnum, g.gametitle, g.gametitle_eng, g.totalviewcount, g.release_year, g.bgg_thumbnail, ga.fullname AS gamephoto_url, b.${filter} as nameStr, gr.rate, g.game_rank, gr.weight, gr.tot, g.theme, ROW_NUMBER() OVER (ORDER BY g.create_date DESC) AS rcount FROM game g LEFT JOIN ( SELECT gnum, fullname FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY gnum ORDER BY create_date DESC) rnum, gnum, fullname FROM game_attach ) WHERE rnum = 1 ) ga ON g.gnum = ga.gnum LEFT JOIN ( SELECT gnum, avg(rating) as rate, avg(weight) as weight, count(*) as tot FROM game_rating GROUP BY gnum ) gr ON g.gnum = gr.gnum left join (
select gnum, f.${filter}, f.anum num from ${mappingTable} m, ${filter} f where m.anum = f.anum
select gnum, f.${filter}, f.cnum num from game_category m, category f where m.cnum = f.cnum
select gnum, f.${filter}, f.dnum num from ${mappingTable} m, ${filter} f where m.dnum = f.dnum
select gnum, f.${filter}, f.mnum num from ${mappingTable} m, ${filter} f where m.mnum = f.mnum
select gnum, f.${filter}, f.pnum num from ${mappingTable} m, ${filter} f where m.pnum = f.pnum
) b on g.gnum = b.gnum WHERE g.del = 'n' and b.num=#{num}
ORDER BY rcount ) a ) WHERE rn BETWEEN #{start} AND #{end}

SELECT gnum, bggnum, gametitle, gametitle_eng, totalviewcount, release_year, gamephoto_url,BGG_THUMBNAIL, GAME_RANK, rate, weight, tot, theme FROM ( SELECT g.gnum, g.bggnum, g.gametitle, g.gametitle_eng, g.totalviewcount, g.release_year, g.theme, ga.fullname AS gamephoto_url, g.BGG_THUMBNAIL, g.GAME_RANK, gr.weight, gr.rate, gr.tot FROM game g LEFT JOIN ( SELECT gnum, fullname FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY gnum ORDER BY create_date DESC) rnum, gnum, fullname FROM game_attach ) WHERE rnum = 1 ) ga ON g.gnum = ga.gnum LEFT JOIN ( SELECT gnum, avg(rating) as rate, avg(weight) as weight, count(*) as tot FROM game_rating GROUP BY gnum ) gr ON g.gnum = gr.gnum left join (
select gnum, exnum from EXPANSION ) ex
select gnum, renum from REIMPLEMENT ) re
on g.gnum = ${filternum} WHERE g.del = 'n' and ${filter}.GNUM= #{num}
on g.gnum = ${filter}.gnum WHERE g.del = 'n' and ${filternum}= #{num}
ORDER BY game_rank )

mapper.update:
update game
set del = 'y', update_date = sysdate, update_user = #{userid}, confirmed= 'allow'
where gnum=#{gnum}

mapper.select:
select gametitle
from (select gnum, gametitle
	  from   game
	  where  del='n'
	  ) g, expansion e
where g.gnum = e.exnum and e.gnum = #{gnum}

select gametitle
from (select gnum, gametitle
	  from   game
	  where  del='n'
	  ) g, reimplement r
where g.gnum = r.renum and r.gnum = #{gnum}

		select  nvl(max(g.gnum),0)
		from    expansion e,
        		(select gnum,GAMETITLE
         		 from   GAME
         		 where  del = 'n' and GAMETITLE = #{expansion}) g
		where   e.exnum = g.gnum and e.gnum = #{gnum}

		select  nvl(max(g.gnum),0)
		from    reimplement r,
        		(select gnum,GAMETITLE
         		 from   GAME
         		 where  del = 'n' and GAMETITLE = #{expansion}) g
		where   r.renum = g.gnum and r.gnum = #{gnum}

select 	*
from	(
		select 	a.*, rownum rn
		from	(
				select 	GNUM,GAMETITLE,GAMETITLE_ENG,BGG_THUMBNAIL,RELEASE_YEAR, game_rank, bggnum, theme
				from 	game
				where 	del = 'n'
				order by game_rank
				) a
		)
where	rn between 1 and 10

SELECT gnum, bggnum, gametitle, gametitle_eng, totalviewcount, release_year, bgg_thumbnail, game_rank, gamephoto_url, rate, weight, tot, theme, rn FROM ( SELECT gnum, bggnum, gametitle, gametitle_eng, totalviewcount, release_year, bgg_thumbnail, game_rank, gamephoto_url, rate, weight, tot, theme, rownum rn FROM ( SELECT g.gnum, g.bggnum, g.gametitle, g.gametitle_eng, g.totalviewcount, g.release_year,g.theme, g.bgg_thumbnail, g.game_rank, ga.fullname AS gamephoto_url, gr.rate, gr.weight, gr.tot FROM ( SELECT gnum, bggnum, gametitle, gametitle_eng, totalviewcount, release_year, bgg_thumbnail, game_rank,theme FROM game WHERE regexp_like(GAMETITLE, #{gameKeyword},'i') AND del = 'n' UNION SELECT gnum, bggnum, gametitle, gametitle_eng, totalviewcount, release_year, bgg_thumbnail, game_rank,theme FROM game WHERE regexp_like(GAMETITLE_ENG, #{gameKeyword},'i') AND del = 'n' ) g LEFT JOIN ( SELECT gnum, fullname FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY gnum ORDER BY create_date DESC) rnum, gnum, fullname FROM game_attach ) WHERE rnum = 1 ) ga ON g.gnum = ga.gnum LEFT JOIN ( SELECT gnum, avg(rating) AS rate, avg(weight) AS weight, count(*) AS tot FROM game_rating GROUP BY gnum ) gr ON g.gnum = gr.gnum ORDER BY CASE WHEN regexp_like(GAMETITLE,'^'||#{gameKeyword} ,'i') THEN 1 WHEN regexp_like(GAMETITLE_ENG,'^'||#{gameKeyword},'i') THEN 2 ELSE 3 END, GAME_RANK ) )
WHERE rn BETWEEN 1 AND 4
WHERE rn between #{start} and #{end}

select	fullname
from	GAME_ATTACH
where	gnum=#{gnum}

mapper.update:
update game
set confirmed='deny', update_date = sysdate, update_user = #{userid}, del='y'
where gnum=#{gnum}

mapper.select:
select 	count(*)
from 	game
where	del = 'n' and theme=#{sort}
