mapper.update:
update gathering
set show = 'n'
where gathering_id = #{gathering_id}

mapper.select:
		select nvl(
		(select min(inner_order) from gathering_reply
		where parent_reply = #{parent_reply} and depth <= #{depth}
		and inner_order > #{inner_order}
		)
		,
		(
		select max(inner_order)+1
		from gathering_reply
		where parent_reply = #{parent_reply}
		)
		)
   	from dual

select reply_id, parent_reply, depth, inner_order
from gathering_reply
where reply_id = #{mother_reply}

select count(*)
from gathering_reply
where gathering_id = #{gathering_id}

mapper.update:
update gathering set update_date = sysdate, title = #{title}, gathering_date = #{gathering_date}, address1 = #{address1}, address2 = #{address2}, address3 = #{address3}, place_name = #{place_name}, maxpeople = #{maxPeople}, attendsystem = #{attendSystem}, note = #{note}, gathering_content = #{gathering_content}, lat = #{lat}, lng=#{lng},
question = #{question}
question = ''
where gathering_id = #{gathering_id}

mapper.select:
select creator_id
from gathering_reply
where reply_id = #{reply_id}

mapper.update:
update gathering_reply
set reply_text = #{reply_text}, updater_id = #{updater_id}, update_date = sysdate
where reply_id = #{reply_id}

update gathering_reply
set show = 'n', updater_id = #{updater_id}, update_date = sysdate
where reply_id = #{reply_id}

update attendee
set last_visit = #{date}
where gathering_id = #{gathering_id} and user_id = #{user_id}

mapper.select:
select gathering_id, post_date, title, gathering_date, address1, address2, view_count, maxpeople, attendee_count, rn from ( select gathering_id, post_date, title, gathering_date, address1, address2, view_count, maxpeople, attendee_count, rownum rn from ( select gathering_id, post_date, title, gathering_date, address1, address2, view_count,maxpeople, ( select count(*) from attendee where gathering_id = g.gathering_id and type = 'a' ) as attendee_count from gathering g where regexp_like(title, #{gameKeyword},'i') AND g.show = 'y' order by CASE WHEN regexp_like(title, '^'||#{gameKeyword},'i') THEN 1 ELSE 2 END , gathering_date desc ) )
where rn between 1 and 6
where rn between #{start} and #{end}
