mapper.select:
select * from tb_comments
where tb_num = #{tb_num}
order by create_date desc

mapper.update:
update tb_comments set content = #{content},
   	update_date = sysdate, update_user = #{create_user}
where reply_reg_num = #{reply_reg_num}

mapper.delete:
update tb_comments set del = 'Y',
	update_date = sysdate, update_user = #{update_user}
where reply_reg_num = #{reply_reg_num}
