mapper.sql:
select *
from (select rownum as rn, A.*
      from (

choose.when:
where del = 'N'

    and category = #{select_category}

and ${search_option} like #{keyword}

choose.otherwise:
where del = 'N'
and category = #{select_category}
and (create_user like #{keyword} or title like #{keyword} or description like #{keyword})

mapper.delete:
update t_board
set del = #{Y}
where tb_num = #{tb_num}

mapper.update:
update t_board
set view_count = view_count + 1
where tb_num = #{tb_num}

update t_board
set re_count = re_count + 1
where tb_num = #{tb_num}
